import { Injectable } from '@nestjs/common';
import mysqlx from '@mysql/xdevapi';
import bcrypt from 'bcrypt';
import validator from 'validator';


var jwt = require('jsonwebtoken');
// import jwt from 'jsonwebtoken';
const JWT_KEY = 'adwdadnwundkamdiwdkamdiw';

var myTable;
var session;
@Injectable()

export class UsersService {
    async connectDB(){
        session = await mysqlx
          .getSession({
            // user: 'root',
            // password: '123456',
            // host: 'localhost',
            // port: 33060
            host: '192.168.10.137',
            port: 33060,        
            user: 'chungpv', 
            password: '1',
          })
        return session;
    }

    close() {
        if (session) {
            session.close();
        }
    }

    async getSchema(){
        myTable = await session.getSchema('mydb').getTable('users');
        return myTable; 
    }

    async insertUser(email, password, name, phone, address, level){
        await this.connectDB();
        await this.getSchema();
        if(!validator.isEmail(email) || validator.isEmpty(email)){
            throw "Email khng hp l!";
        }
        if(validator.isEmpty(name,{ ignore_whitespace: true })){
            throw "Name khng hp l!";
        }
        myTable.insert(['email','password','name','phone','address','level'])
          .values([email,password,name,phone,address,level])
          .execute();
    }

    async updateUser(id, newEmail, newPassword, newName, newPhone, newAddress, newLevel){
        await this.connectDB();
        await this.getSchema();
        myTable.update()
          .set('email',newEmail)
          .set('password',newPassword)
          .set('name',newName)
          .set('phone',newPhone)
          .set('address',newAddress)
          .set('level',newLevel)
          .where('id = :param')
          .bind('param',id)
          .execute();
    }

    async deleteUser(id){
        await this.connectDB();
        await this.getSchema();
        myTable.delete()
        .where('id = :param')
        .bind('param',id)
        .execute();
    }

    async findAll(){
        await this.connectDB();
        await this.getSchema();
        let result = await myTable.select()
        .execute();  
        return result.fetchAll(); 
    }

    async findById(id){
        await this.connectDB();
        await this.getSchema();
        let result = await myTable.select()
        .where('id = :param')
        .bind('param',id)
        .execute();
        return result.fetchAll();
        // await this.connectDB();
        // await session.sql('use mydb').execute();
        // var result = await session.sql('SELECT * FROM users').execute();

        // console.log(result.fetchAll());
    }

    async bcryptPass(string){
        var saltRounds = 10;
        let password = await bcrypt.hash(string, saltRounds);
        return password;
    }

    // async passCompare(password){
    //     return bcrypt.compare(password, hash);
    // }

    async registerUser(email, password, name, phone, address, level, session) {
        await this.connectDB();
        await this.getSchema();
        if(!validator.isEmail(email) || validator.isEmpty(email)){
            throw "Email khng hp l!";
        }
        if(validator.isEmpty(name,{ ignore_whitespace: true })){
            throw "Name khng hp l!";
        }
        
        let findEmail = await myTable.select()
                                        .where('email = :param')
                                        .bind('param',email)
                                        .execute();
        let user = findEmail.fetchAll();
        //console.log(findEmail.fetchAll().length);
        console.log(user.length);
          if(user.length !== 0){
              throw "Email NOT FOUND"
          }
        let register = await myTable.insert(['email','password','name','phone','address','level'])
          .values([email,password,name,phone,address,level])
          .execute();
        
        }

    async loginUser(email, password, session) {
        await this.connectDB();
        await this.getSchema();
        if(!validator.isEmail(email) || validator.isEmpty(email)){
            throw "Email khng hp l!";
        }
        console.log(email);
        let findEmail = await myTable.select()
        .where('email = :param')
        .bind('param',email)
        .execute();
        let user = findEmail.fetchAll();
        //console.log(findEmail.fetchAll().length);
        if(user.length == 0){
            throw "Email NOT FOUND"
        }
        let data = bcrypt.compareSync(password, user[0][2]);
        if( data == false){
            throw "Passwor NOT FOUND"
        }
        // console.log(session);
        if(!session.user){
            session.user = {
            id: user[0][0],
            email: user[0][1],
            };
        }else{
            session.user = {
                id: user[0][0],
                email: user[0][1],
            }
        }
        console.log(session);
        console.log(user[0][0]);
        return session;
    }

    async logout(id) {
