import { Controller,Session, Get, Query, Dependencies, Post, Bind, Param, Req, Put, Delete, Res } from '@nestjs/common';
import { UsersService } from './users.service';
import validator from 'validator';

const session = require('express-session');
const redis = require('redis');
const redisStore = require('connect-redis')(session);	

const redisClient = redis.createClient();

//
// var session = require('express-session')
// import session from 'express-session'
// console.log(session);



@Controller('users')
// @Controller()
@Dependencies(UsersService)
export class UsersController {
    constructor(usersService){
        this.usersService = usersService;
    }

    @Get('abc')
    @Bind(Req())
    getViews(req) {
        console.log('aaaaa'+ req.session);
        // console.log(session);
        // // session.views = (session.views || 0) + 1;
        // // return session.views;
        // if(!session.user){
        //     session.user = {
        //       id : Math.random()
        //     };
        //     console.log(session.user);
        // }
    }

    @Get('gettest')
    @Bind(Req())
    getViews(req) {
        return req.headers.cookie;
    }


    @Get()
    @Bind(Req(), Res())
    async findAll(req, res){
        try {
            let resutl = await this.usersService.findAll();
            req.session.Users = resutl;
            return res.json({
                mess: "success",
                data: resutl
            });
        } catch (error) {
            console.log(error)
        }
    }

    @Get(':id')
    @Bind(Param(), Res())
    async findById(params,res){
        // console.log(params.id);
       try {
        let resutl = await this.usersService.findById(params.id);
        return res.json({
            mess: "success",
            data: resutl
        });
       } catch (error) {
           console.log(error);
       }
    }

    @Post()
    @Bind(Req(), Res())
    async insertUser(req,res){
        try {
            if(validator.isEmpty(req.body.password,{ ignore_whitespace: true })){
                throw "Pass khng hp l!";
            }
            let email = req.body.email;
        //  req.body.password;
            let password = await this.usersService.bcryptPass(req.body.password);
            let name = req.body.name;
            let phone = req.body.phone;
            let address = req.body.address;
            let level = req.body.level;
            
            // console.log(password);
            await this.usersService.insertUser(email, password, name, phone, address, level);
            return res.json({
                mess:"success",
                data: req.body
            });
        } catch (error) {
            return res.json({
                mess: error
            });
        }

        // console.log(req.body.name);
    }

    @Put(':id')
    @Bind(Param(), Req(), Res())
    async updateUser(params, req, res) {
        try {
            let email = req.body.email;
        //  req.body.password;
            let password = await this.usersService.bcryptPass(req.body.password);
            let name = req.body.name;
            let phone = req.body.phone;
            let address = req.body.address;
            let level = req.body.level;
            
            // console.log(password);
            await this.usersService.updateUser(params.id, email, password, name, phone, address, level);
            return res.json({
                mess:"success",
                data: req.body
            });
        } catch (error) {
            return res.json({
                mess: error
            });
        }

    }

    @Delete('find/:id')
    @Bind(Param(), Res())
    removeUsers(params,res) {
       try {
            this.usersService.deleteUser(params.id);
            return res.json({
                mess:"success",
            });
       } catch (error) {
            return res.json({
                mess: error
            });
       }
    }

    @Post('register')
    @Bind(Session(), Req(), Res())
    async register(session, req, res) {
        try {
            if(validator.isEmpty(req.body.password,{ ignore_whitespace: true })){
                throw "Pass khng hp l!";
            }
            let email = req.body.email;
            let password = await this.usersService.bcryptPass(req.body.password);
            let name = req.body.name;
            let phone = req.body.phone;
            let address = req.body.address;
            let level = req.body.level;
            
            // console.log(password);
            let register = await this.usersService.registerUser(email, password, name, phone, address, level, session);
            return res.json({
                mess:"success",
                code: 200,
                data: register
            });
        } catch (error) {
            console.log(error);
            return res.json({
                mess: "erro",
                code: 404,
                data: error,
            });
        }
    }

    @Post('login')
    @Bind(Req(), Res())
    async login(req,res) {
        try {
            if(validator.isEmpty(req.body.password,{ ignore_whitespace: true })){
                throw "Pass khng hp l!";
            }
            let email = req.body.email;
            // let password = await this.usersService.bcryptPass(req.body.password);
            let password = req.body.password;
            let session = req.session;
            console.log(session);
            let login = await this.usersService.loginUser(email, password, session);
            // console.log(req.headers.cookie);
            return res.json({
                mess:"success",
                code: 200,
                data: login
            });
        } catch (error) {
            console.log(error);
            return res.json({
                mess: "erro",
                code: 404,
                data: error,
            });
        }
    }

    @Delete('logout')
    @Bind(Req(), Res())
    async logout(req, res) {
        try {
            console.log("11111111111111111111111111111bdhjbsdjbj");
            // let token = req.token;
            // console.log(req.session.user.id);
            let id = req.session.user.id;
            console.log(id);
            let logout = await this.usersService.logout(id);
            return res.json({
                mess:"success",
                code: 200,
                data: logout
                });
        } catch (error) {
            console.log(error)
        }
    }

    // link: https://anonystick.com/blog-developer/vi-du-ve-nodejs-session-su-dung-express-session-2019110742210630

}

